<?php	require_once('recaptchalib.php');	$privatekey = "6LcJONQSAAAAAEe69OJ1uXqOWvVAOy7kkfHr1iUo";	$resp = recaptcha_check_answer ($privatekey, $_SERVER["REMOTE_ADDR"], $_POST["recaptcha_challenge_field"], $_POST["recaptcha_response_field"]);	$recommend = false;	if ($_GET["i"] == "recommend") $recommend = true;	if ($_POST["recommend"] == "true") $recommend = true;	if ($_POST["send"] == "true")	{				$send = true;		$error = "";		$sent = false;		if ($_POST["title"] == "" && !$recommend)		{			$send = false;			$error .= "*אינך יכול לשלוח הודעה ללא כותרת.<br />";		}		if ($_POST["name"] == "" && $recommend)		{			$send = false;			$error .= "*אינך יכול לשלוח הודעה ללא כותרת.<br />";		}		if ($_POST["message"] == "")		{			$send = false;			if ($_POST["recommend"] == "true")				$error .= "*אינך יכול לשלוח הודעה ללא תוכן.<br />";			else				$error .= "*אינך יכול לשלוח הודעה ללא תוכן.<br />";		}		else if (!$resp->is_valid)		{			$send = false;			$error .= "*תוכן התמונה שהוקש הינו שגוי. אנא נסה שוב.<br />";		}		if ($send)		{			$to      = "bugalit@gmail.com";			$subject = $_POST["type"]." - ".$_POST["title"];			$message = "Email: ".$_POST["email"]."\n".				"Name: ".$_POST["name"]."\n".				"Subject: ".$_POST["title"]."\n".				"Time: ".date("d/m/Y H:i:s", $_SERVER["REQUEST_TIME"])." - DD/MM/YYYY hh:mm:ss UTC -6\n".				"Refferer: ".$_SERVER["HTTP_REFERER"]."\n".				"User Agent: ".$_SERVER["HTTP_USER_AGENT"]."\n".				"Remote Host: ".gethostbyaddr($_SERVER['REMOTE_ADDR'])."\n".				"Remote Port: ".$_SERVER["REMOTE_PORT"]."\n".				"Remote IP: ".$_SERVER["REMOTE_ADDR"]."\n".				"Full Server Info: ".http_build_query($_SERVER, '', ',')."\n\n\n".				$_POST["message"];				$message = wordwrap($message, 70, '\n', true);			$headers = "From: service@yago.co.il\r\n".               "MIME-Version: 1.0\r\n" .               "Content-type: text/html; charset=UTF-8\r\n"; 			if ($recommend)			{				$sql = "INSERT INTO recommendations (name, recommendation, email, phone, ip, referrer, remotehost, remoteport, useragent, cookie, full_string_info) VALUES ('".				mysql_real_escape_string($_POST['name'])."', '".				mysql_real_escape_string($_POST['message'])."', '".				mysql_real_escape_string($_POST['email'])."', '".				mysql_real_escape_string($_POST['phone'])."', '".				mysql_real_escape_string($_SERVER["REMOTE_ADDR"])."', '".				mysql_real_escape_string($_SERVER["HTTP_REFERER"])."', '".				mysql_real_escape_string(gethostbyaddr($_SERVER['REMOTE_ADDR']))."', '".				mysql_real_escape_string($_SERVER["REMOTE_PORT"])."', '".				mysql_real_escape_string($_SERVER["HTTP_USER_AGENT"])."', '".				mysql_real_escape_string(http_build_query($_SERVER, '', ','))."'".				");";				mysql_query($sql);				$id = mysql_insert_id();				$message .= "\n\n\nTo accept the recomendation enter your password on this page:\n";				$message .= "http://www.yago.co.il/test2/approve_recommendation.php?index=".$id;				$message .= "\n\n\nTo delete the recomendation enter your password on this page:\n";				$message .= "http://www.yago.co.il/test2/remove_recommendation.php?index=".$id;				$subject = "new recommendation";			}			if (mail($to, "=?UTF-8?B?".base64_encode($subject)."?=", $message, $headers))			{				$sent = true;				include_once("Pages/contactsent.php");			}			else mysql_query("DELETE FROM recommendations WHERE index=".$id);		}		$error .= "<br /><br />";	}	if (!$sent)	include_once("Pages/contactmake.php");?>